function makeDropDown($options, $name, $selected, $onchange) {

  	if( $onchange ) $onchange .= " " . $onchange;

	$output = "<select class=\"uniform\" name=\"$name\"$onchange>\r\n";
	foreach ( $options as $value => $description ) {
		$output .= "<option value=\"$value\"";
		if( $selected == $value ) {
			$output .= " selected ";
		}
		$output .= ">$description</option>\n";
	}
	$output .= "</select>";
	return $output;
}

function moveCategoryChange(obj) {

  var value = $(obj).val();

  if (value == 5) $('#movecatlist').show();
  else $('#movecatlist').hide();

}

