operation.replacecode:
if (stripos ( $tpl->result['main'], "[aviable_date" ) !== false AND $news_found) {

  $tpl->result['main'] = preg_replace_callback ( "#\\[aviable_date=['\"](.+?)['\"]\\](.*?)\\[/aviable_date\\]#is", "check_date", $tpl->result['main'] );

} else $tpl->result['main'] = preg_replace ( "#\\[aviable_date=['\"](.+?)['\"]\\](.*?)\\[/aviable_date\\]#is", "", $tpl->result['main'] );

function check_date($matches) {
 	global $curr_news_date, $_TIME;

 	if (is_array($matches)) {

		$params = $matches[1];
		$block = $matches[2];

		if( $params == 'yesterday' ) {

			if( date("Y-m-d", ($_TIME - 86400)) != date("Y-m-d", $curr_news_date) ) $block = "";

		} else if( $params == 'tomorrow' ) {

			if( date("Y-m-d", ($_TIME + 86400)) != date("Y-m-d", $curr_news_date) ) $block = "";

		} else {

			$date1 = strtotime($params);
			if( date("Y-m-d", $date1) != date("Y-m-d", $curr_news_date) ) $block = "";

		}

    } else $block = "";

  	return $block;
}
